mapper.select:
select *
from ATTENDENCE
where u_id = #{u_id}
  and tstatus = #{tstatus}
  and date_format(reg_date, "%Y-%m-%d") = current_date()

select count(*)
from ATTENDENCE
where u_id = #{u_id}
  AND tstatus = #{tstatus}

select *
from ATTENDENCE
where u_id = #{u_id}
  AND ustatus = #{ustatus}

mapper.update:
update ATTENDENCE
set ustatus=1
where reg_date <= now() - interval 2 week;

mapper.select:
select status
from SUBMIT
where u_id = #{u_id}

mapper.update:
update ATTENDENCE
set ustatus = 1
where no = #{a_no}

mapper.select:
select count(*)
from ATTENDENCE
where u_id = #{u_no}

mapper.update:

SET
@currentDateTime = NOW();

insert into ATTENDENCE(u_id, astatus, tstatus, reg_date, mod_date)
select u.id, '3', @currentDateTime, now(), now()
from USER u
where u.id not in (select u_id from ATTENDENCE where HOUR (reg_date) <12)


SET
@currentDateTime = NOW();

insert into ATTENDENCE(u_id, astatus, tstatus, reg_date, mod_date)
select u.id, '3', @currentDateTime, now(), now()
from USER u
where u.id not in (select u_id from ATTENDENCE where HOUR (reg_date) >= 14 AND HOUR (reg_date) < 18)

mapper.select:
select *
from ATTENDENCE
where DATE (date) = CURDATE() AND HOUR (date) >= 18;
